import React from 'react'
import { useLocation } from 'react-router';
import styles from './App.module.css'
import CityMap from './Map/CityMap';
import RectangleMap from './Map/RectangleMap';
import { Link } from 'react-router-dom';
import PolygonMap from './Map/PolygonMap';


function App(props) {
    const location = useLocation()
    const type = location.state.type;
    const dataToApp = location.state.dataArr;
    console.log(dataToApp)
    switch (type) {
        case "City":
            return (
                <div className={styles.App}>       
                    <Link className={styles.back} to={`/`}> Назад </Link>
                    <p>Данные с предыдущей страницы</p>
                    <p>Тип предыдущей формы: {type}</p>
                    <p>Получены данные: {dataToApp[0].value} </p>
                     <CityMap className={styles.Map} pos={[dataToApp[0].lat, dataToApp[0].lon]}></CityMap>
                </div>)
        case "Polygon":
            let polygonPos = [];
            let polygonOutput = '';
            for (let i = 0; i < dataToApp[0].length; ++i) {
                polygonPos[i] = dataToApp[i];
                polygonOutput += dataToApp[0][i][0];
                polygonOutput += ';'
                polygonOutput += dataToApp[0][i][1];
                polygonOutput += ' '
            }
            return (
                <div className={styles.App}>       
                <Link className={styles.back} to={`/`}> Назад </Link>
                <p>Данные с предыдущей страницы</p>
                <p>Тип предыдущей формы: {type}</p>
                <p>Получены данные:<br/> {polygonOutput}</p>
                <PolygonMap pos={dataToApp}/>
        </div>)
        case "Rectangle":
            let rectanglePos = [];
            let output = '';
            for (let i = 0; i < 4; ++i) {
                output += dataToApp[i] + ' ';
                rectanglePos.push(dataToApp[i]);
            }
            return (
                <div className={styles.App}>   
                <Link className={styles.back} to={`/`}> Назад </Link>
                <p>Данные с предыдущей страницы</p>
                <p>Тип предыдущей формы: { type }</p>
                <p>Получены данные: { output }</p>
                <RectangleMap pos={rectanglePos}></RectangleMap>
        </div>)
        default:
            break;
    }
}

export default App
